function redirect() {
  const ua = navigator.userAgent;
  const path = window.location.pathname;
  const search = window.location.search;
  const referer = document.referrer;

  sessionStorage.setItem("origin", referer);
}

var userAgent = navigator.userAgent;
var isIE10 =
  userAgent.indexOf("compatible") > -1 && userAgent.indexOf("MSIE") > -1; //ie11以下
var isEdge = userAgent.indexOf("Edge") > -1 && !isIE10;
var isIE11 =
  userAgent.indexOf("Trident") > -1 && userAgent.indexOf("rv:11.0") > -1;

function isIE() {
  if (!!window.ActiveXObject || "ActiveXObject" in window) {
    return true;
  } else {
    return false;
  }
}

function checkIeBrowser() {
  if (isIE()) {
    console.log("checkIeBrowser", window.location.pathname);
    if (
      (isIE11 || isEdge) &&
      window.location.pathname !== "/s/notSupport"
    ) {
      window.location.href = "/s/notSupport";
    }
  }
}

checkIeBrowser();
redirect();

var _hmt = _hmt || [];
(function () {
  var hm = document.createElement("script");
  hm.src = "https://hm.baidu.com/hm.js?20be40a172d7612207074b5036f5a1eb";
  var s = document.getElementsByTagName("script")[0];
  s.parentNode.insertBefore(hm, s);
})();

var isIframe = window.self !== window.top;
var ua = navigator.userAgent.toLocaleLowerCase();
var isMobile = /(iphone|ipad|ipod|mobile|android|phone)/.test(ua);
var pathname = window.location.pathname;
if (!isMobile) {
  document.documentElement.style.fontSize = "74.7777px";
  // WEB端打开移动端页面，自动跳转WEB端路由
  if (pathname === "/s/login" || pathname === "/s/home") {
    window.location.href = "/";
  }
} else {
  // 移动端页面打开WEB端，自动跳转移动端页面路由
  if (pathname === "/" || pathname === "") {
    window.location.href = "/s/home";
  }
}

var performance =
  window.performance || window.msPerformance || window.webkitPerformance;
if (performance && performance.timing) {
  var timing = performance.timing;
  console.log("重定向耗时：", timing.redirectEnd - timing.redirectStart);
  console.log(
    "DNS查询耗时：",
    timing.domainLookupEnd - timing.domainLookupStart
  );
  console.log("TCP链接耗时：", timing.connectEnd - timing.connectStart);
  console.log(
    "HTTP请求耗时：",
    timing.responseEnd - timing.responseStart
  );
  console.log(
    "解析dom树耗时：",
    timing.domComplete - timing.domInteractive
  );
  console.log(
    "白屏时间：",
    timing.responseStart - timing.navigationStart
  );
  console.log(
    "DOM ready时间：",
    timing.domContentLoadedEventEnd - timing.navigationStart
  );
  console.log(
    "onload时间：",
    timing.loadEventEnd - timing.navigationStart
  );
}
